mapper.update:
update product
set PROD_NAME = #{prodName}, PROD_PRICE = #{prodPrice}, PROD_INFO = #{prodInfo}
where PROD_NO = #{prodNo}

mapper.select:
select PROD_NO 
from cart
where MEMB_ID = #{membId}

select PROD_NAME, PROD_PRICE,
		MEMB_ID ,CART_QTY, CART_PRICE, c.PROD_NO
from product p join cart c
	on p.PROD_NO = c.PROD_NO
where c.MEMB_ID = ${membId}
	and c.PROD_NO = #{prodNo}
